query.sql:
SELECT "AUDIT_TRAIL", "RAC_INSTANCE", "CLEANUP_TIME", "DELETE_COUNT", "WAS_FORCED" FROM(
select * from DBA_AUDIT_MGMT_CLEAN_EVENTS
)

SELECT "JOB_NAME", "JOB_STATUS", "AUDIT_TRAIL", "JOB_FREQUENCY", "USE_LAST_ARCHIVE_TIMESTAMP", "JOB_CONTAINER" FROM(
select * from dba_audit_mgmt_cleanup_jobs
)

SELECT "PARAMETER_NAME", "PARAMETER_VALUE", "AUDIT_TRAIL" FROM(
select * from dba_audit_mgmt_config_params
)
